import React, { Component } from 'react';
import axios from 'axios';

export default class Form extends Component {
  constructor(props) {
    super(props);
    
    this.state = {
      vendor: '',
      date: '',
      amount: '',
      currency: '',
      tax_rate: '',
      tax_amount: '',
      category: '',
      paid_with: '',
      country: '',
      cost_center: '',  
      comments: '',
      file: null,
      fileLoaded: 0    
    };
    
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const inputValue = event.target.value;
    const stateField = event.target.name;
    
    this.setState({
      [stateField]: inputValue,
    });
    
    console.log(this.state);
  }

  onChangeFileHandler(event) {
    console.log(event.target.files[0])

    this.setState({
      file: event.target.files[0],
      fileLoaded: 0,
    })
  }

  async handleSubmit(event) {
    console.log(event);
    event.preventDefault();
    const { vendor, date, amount, file } = this.state;
    console.log("vendor: " + vendor + ", date: " + date);
    console.log("file: " + file)
    await axios.post(
      'https://k42g0igjed.execute-api.eu-central-1.amazonaws.com/DEV/',
      { 
        username: "react",
        date: `${date}`,
        amount: `${amount}`,
        file: `${file}`
      }
    )
    .then(res => {
      console.log(res.statusText)
    });
  }
  



  render() {
    const style = {
      border: '1px solid blue',
      textAlign: 'center',
      verticalAlign: 'middle'
    }

    return (
      <div style={style}>
        <form onSubmit={this.handleSubmit}>
          <label>vendor:</label>
          <input
            type="text"
            name="vendor"
            onChange={this.handleChange}
            value={this.state.vendor}
          /><br/>

          <label>date:</label>
          <input
            type="text"
            name="date"
            onChange={this.handleChange}
            value={this.state.date}
          /><br/>

          <label>amount:</label>
          <input
            type="text"
            name="amount"
            onChange={this.handleChange}
            value={this.state.amount}
          /><br/>

          <label>currency:</label>
          <input
            type="text"
            name="currency"
            onChange={this.handleChange}
            value={this.state.currency}
          /><br/>
          
          <label>tax_rate:</label>
          <input
            type="text"
            name="tax_rate"
            onChange={this.handleChange}
            value={this.state.tax_rate}
          /><br/>                      

          <label>tax_amount:</label>
          <input
            type="text"
            name="tax_amount"
            onChange={this.handleChange}
            value={this.state.tax_amount}
          /><br/>  

          <label>category:</label>
          <input
            type="text"
            name="category"
            onChange={this.handleChange}
            value={this.state.category}
          /><br/>  

          <label>paid_with:</label>
          <input
            type="text"
            name="paid_with"
            onChange={this.handleChange}
            value={this.state.paid_with}
          /><br/>  

          <label>country:</label>
          <input
            type="text"
            name="country"
            onChange={this.handleChange}
            value={this.state.country}
          /><br/>  

          <label>cost_center:</label>
          <input
            type="text"
            name="cost_center"
            onChange={this.handleChange}
            value={this.state.cost_center}
          /><br/> 

          <label>comments:</label>
          <input
            type="text"
            name="comments"
            onChange={this.handleChange}
            value={this.state.comments}
          /><br/>  

          <input 
            type="file" 
            name="file"
            onChange={this.onChangeFileHandler} 
          /><br/> 
           


          <button type="submit">Send</button>
        </form>
      </div>
    );
  }
}
