import React, { Component } from 'react';
import axios from 'axios';

export default class Form extends Component {
  constructor(props) {
    super(props);
    this.state = {
      vendor: '',
      amount: '',
      currency: '',
      tax_rate: '',
      tax_amount: '',
      category: '',
      paid_with: '',
      country: '',
      cost_center: '',  
      comments: ''    
    };
    this.handleSubmit = this.handleSubmit.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  handleChange(event) {
    const inputValue = event.target.value;
    const stateField = event.target.name;
    this.setState({
      [stateField]: inputValue,
    });
    console.log(this.state);
  }

  async handleSubmit(event) {
    event.preventDefault();
    const { name, message } = this.state;
    await axios.post(
      'https://38jl3av55c.execute-api.eu-central-1.amazonaws.com/default/addExpense',
      { 
        username: "piotrjacak@reload-hr.com",
        key1: `${name}, ${message}` 
      }
    );
  }
  



  render() {
    const style = {
      border: '1px solid blue',
      textAlign: 'center',
      verticalAlign: 'middle'
    }

    return (
      <div style={style}>
        <form onSubmit={this.handleSubmit}>
          <label>vendor:</label>
          <input
            type="text"
            name="vendor"
            onChange={this.handleChange}
            value={this.state.vendor}
          /><br/>

          <label>amount:</label>
          <input
            type="text"
            name="amount"
            onChange={this.handleChange}
            value={this.state.amount}
          /><br/>

          <label>currency:</label>
          <input
            type="text"
            name="currency"
            onChange={this.handleChange}
            value={this.state.currency}
          /><br/>
          
          <label>tax_rate:</label>
          <input
            type="text"
            name="tax_rate"
            onChange={this.handleChange}
            value={this.state.tax_rate}
          /><br/>                      

          <label>tax_amount:</label>
          <input
            type="text"
            name="tax_amount"
            onChange={this.handleChange}
            value={this.state.tax_amount}
          /><br/>  

          <label>category:</label>
          <input
            type="text"
            name="category"
            onChange={this.handleChange}
            value={this.state.category}
          /><br/>  

          <label>paid_with:</label>
          <input
            type="text"
            name="paid_with"
            onChange={this.handleChange}
            value={this.state.paid_with}
          /><br/>  

          <label>country:</label>
          <input
            type="text"
            name="country"
            onChange={this.handleChange}
            value={this.state.country}
          /><br/>  

          <label>cost_center:</label>
          <input
            type="text"
            name="cost_center"
            onChange={this.handleChange}
            value={this.state.cost_center}
          /><br/> 

          <label>cost_center:</label>
          <input
            type="text"
            name="cost_center"
            onChange={this.handleChange}
            value={this.state.cost_center}
          /><br/>

          <label>comments:</label>
          <input
            type="text"
            name="comments"
            onChange={this.handleChange}
            value={this.state.comments}
          /><br/>  

           


          <button type="submit">Send</button>
        </form>
      </div>
    );
  }
}
